#' AHP for survey data
#'
#' 
#' @param data The data as a data frame.
#' @param vars .
#' @param atts .
#' @param ap .
#' @param method .
#' @param aj .
#' @param method1 .
#' @param plot1 .
#' @param plot2 .
#' @param angle a number from 0 to 90 defining the angle of the x-axis labels,
#'   where 0 degrees represents completely horizontal labels.
#' @param width1 .
#' @param height1 .
#' @param width2 .
#' @param height2 .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$instructions} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$text} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$ap} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$aj} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$cr} \tab \tab \tab \tab \tab an output \cr
#'   \code{results$plot1} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot2} \tab \tab \tab \tab \tab an image \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$ap$asDF}
#'
#' \code{as.data.frame(results$ap)}
#'
#' @export
ahpsurvey <- function(
    data,
    vars,
    atts = "cult, fam, house, jobs, trans",
    ap = TRUE,
    method = "eigen",
    aj = FALSE,
    method1 = "geometric",
    plot1 = FALSE,
    plot2 = FALSE,
    angle = 0,
    width1 = 500,
    height1 = 500,
    width2 = 500,
    height2 = 500) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("ahpsurvey requires jmvcore to be installed (restart may be required)")

    if ( ! missing(vars)) vars <- jmvcore::resolveQuo(jmvcore::enquo(vars))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(vars), vars, NULL))


    options <- ahpsurveyOptions$new(
        vars = vars,
        atts = atts,
        ap = ap,
        method = method,
        aj = aj,
        method1 = method1,
        plot1 = plot1,
        plot2 = plot2,
        angle = angle,
        width1 = width1,
        height1 = height1,
        width2 = width2,
        height2 = height2)

    analysis <- ahpsurveyClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

