#' Velicer MAP Test
#'
#' 
#' @param data The data as a data frame.
#' @param vars .
#' @param type .
#' @param eigen .
#' @param avgCorr .
#' @param screePlot .
#' @param MapCurvePlot .
#' @param emp .
#' @param hull .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$instructions} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$text} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$text1} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$eigen} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$avgCorr} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$empkcTable} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$screePlot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$MapCurvePlot} \tab \tab \tab \tab \tab an image \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$eigen$asDF}
#'
#' \code{as.data.frame(results$eigen)}
#'
#' @export
map <- function(
    data,
    vars,
    type = "pearson",
    eigen = TRUE,
    avgCorr = FALSE,
    screePlot = FALSE,
    MapCurvePlot = FALSE,
    emp = FALSE,
    hull = FALSE) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("map requires jmvcore to be installed (restart may be required)")

    if ( ! missing(vars)) vars <- jmvcore::resolveQuo(jmvcore::enquo(vars))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(vars), vars, NULL))


    options <- mapOptions$new(
        vars = vars,
        type = type,
        eigen = eigen,
        avgCorr = avgCorr,
        screePlot = screePlot,
        MapCurvePlot = MapCurvePlot,
        emp = emp,
        hull = hull)

    analysis <- mapClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

