#' Partial Correlation
#'
#' 
#' @param data The data as a data frame.
#' @param vars .
#' @param ctrlvars .
#' @param sidSig .
#' @param scale .
#' @param shwSig .
#' @param flgSig .
#' @param cen .
#' @param plot .
#' @param model .
#' @param layout .
#' @param shape .
#' @param plot2 .
#' @param width .
#' @param height .
#' @param width1 .
#' @param height1 .
#' @param angle .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$instructions} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$text2} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$matrix} \tab \tab \tab \tab \tab correlation matrix table \cr
#'   \code{results$plot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot2} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$cen} \tab \tab \tab \tab \tab a table \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$matrix$asDF}
#'
#' \code{as.data.frame(results$matrix)}
#'
#' @export
partial <- function(
    data,
    vars,
    ctrlvars,
    sidSig = "twotailed",
    scale = "raw0",
    shwSig = FALSE,
    flgSig = TRUE,
    cen = FALSE,
    plot = TRUE,
    model = "glasso",
    layout = "spring",
    shape = "circle",
    plot2 = FALSE,
    width = 500,
    height = 500,
    width1 = 500,
    height1 = 500,
    angle = 0) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("partial requires jmvcore to be installed (restart may be required)")

    if ( ! missing(vars)) vars <- jmvcore::resolveQuo(jmvcore::enquo(vars))
    if ( ! missing(ctrlvars)) ctrlvars <- jmvcore::resolveQuo(jmvcore::enquo(ctrlvars))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(vars), vars, NULL),
            `if`( ! missing(ctrlvars), ctrlvars, NULL))


    options <- partialOptions$new(
        vars = vars,
        ctrlvars = ctrlvars,
        sidSig = sidSig,
        scale = scale,
        shwSig = shwSig,
        flgSig = flgSig,
        cen = cen,
        plot = plot,
        model = model,
        layout = layout,
        shape = shape,
        plot2 = plot2,
        width = width,
        height = height,
        width1 = width1,
        height1 = height1,
        angle = angle)

    analysis <- partialClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

