#' Ordinal Correlation
#'
#' 
#' @param data The data as a data frame.
#' @param vars .
#' @param type .
#' @param scale .
#' @param cor .
#' @param plot .
#' @param plot1 .
#' @param plot2 .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$instructions} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$matrix} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$plot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot2} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot1} \tab \tab \tab \tab \tab an image \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$matrix$asDF}
#'
#' \code{as.data.frame(results$matrix)}
#'
#' @export
rank <- function(
    data,
    vars,
    type = "spearman",
    scale = "raw",
    cor = TRUE,
    plot = FALSE,
    plot1 = FALSE,
    plot2 = FALSE) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("rank requires jmvcore to be installed (restart may be required)")

    if ( ! missing(vars)) vars <- jmvcore::resolveQuo(jmvcore::enquo(vars))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(vars), vars, NULL))


    options <- rankOptions$new(
        vars = vars,
        type = type,
        scale = scale,
        cor = cor,
        plot = plot,
        plot1 = plot1,
        plot2 = plot2)

    analysis <- rankClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

