raterBase <- if (requireNamespace("jmvcore", quietly=TRUE)) R6::R6Class(
    "raterBase",
    inherit = jmvcore::Analysis,
    public = list(
        initialize = function(options, data=NULL, datasetId="", analysisId="", revision=0) {
            super$initialize(
                package = "seolmatrix",
                name = "rater",
                version = c(1,0,0),
                options = options,
                results = raterResults$new(options=options),
                data = data,
                datasetId = datasetId,
                analysisId = analysisId,
                revision = revision,
                pause = NULL,
                completeWhenFilled = FALSE,
                requiresMissings = FALSE,
                weightsSupport = 'auto')
        }))

#' Rater Reliability
#'
#' 
#' @param data The data as a data frame.
#' @param vars .
#' @param model .
#' @param type .
#' @param unit .
#' @param interrater .
#' @param icc .
#' @param bicc .
#' @param ic .
#' @param ftest .
#' @param ggm .
#' @param par .
#' @param fk .
#' @param cw .
#' @param ek .
#' @param krip .
#' @param method .
#' @param t .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$instructions} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$interrater} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$fk} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$ek} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$cw} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$icc} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$bicc} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$ic} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$ftest} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$plot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot1} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$krip} \tab \tab \tab \tab \tab a table \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$interrater$asDF}
#'
#' \code{as.data.frame(results$interrater)}
#'
#' @export
rater <- function(
    data,
    vars,
    model = "oneway",
    type = "agreement",
    unit = "single",
    interrater = FALSE,
    icc = FALSE,
    bicc = FALSE,
    ic = FALSE,
    ftest = FALSE,
    ggm = FALSE,
    par = FALSE,
    fk = FALSE,
    cw = FALSE,
    ek = FALSE,
    krip = FALSE,
    method = "nominal",
    t = "col") {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("rater requires jmvcore to be installed (restart may be required)")

    if ( ! missing(vars)) vars <- jmvcore::resolveQuo(jmvcore::enquo(vars))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(vars), vars, NULL))


    options <- raterOptions$new(
        vars = vars,
        model = model,
        type = type,
        unit = unit,
        interrater = interrater,
        icc = icc,
        bicc = bicc,
        ic = ic,
        ftest = ftest,
        ggm = ggm,
        par = par,
        fk = fk,
        cw = cw,
        ek = ek,
        krip = krip,
        method = method,
        t = t)

    analysis <- raterClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

