#' Rater Reliability
#'
#' 
#' @param data The data as a data frame.
#' @param vars .
#' @param model .
#' @param type .
#' @param unit .
#' @param interrater .
#' @param ic .
#' @param ftest .
#' @param icc .
#' @param bicc .
#' @param ggm .
#' @param par .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$instructions} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$interrater} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$icc} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$bicc} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$plot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot1} \tab \tab \tab \tab \tab an image \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$interrater$asDF}
#'
#' \code{as.data.frame(results$interrater)}
#'
#' @export
rater <- function(
    data,
    vars,
    model = "oneway",
    type = "agreement",
    unit = "single",
    interrater = TRUE,
    ic = FALSE,
    ftest = FALSE,
    icc = FALSE,
    bicc = FALSE,
    ggm = FALSE,
    par = FALSE) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("rater requires jmvcore to be installed (restart may be required)")

    if ( ! missing(vars)) vars <- jmvcore::resolveQuo(jmvcore::enquo(vars))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(vars), vars, NULL))


    options <- raterOptions$new(
        vars = vars,
        model = model,
        type = type,
        unit = unit,
        interrater = interrater,
        ic = ic,
        ftest = ftest,
        icc = icc,
        bicc = bicc,
        ggm = ggm,
        par = par)

    analysis <- raterClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

