#' Repeated Measures Correlation
#'
#' 
#' @param data .
#' @param id .
#' @param dep .
#' @param covs .
#' @param cc .
#' @param plot .
#' @param width .
#' @param height .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$instructions} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$cc} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$plot} \tab \tab \tab \tab \tab an image \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$cc$asDF}
#'
#' \code{as.data.frame(results$cc)}
#'
#' @export
rmc <- function(
    data,
    id,
    dep,
    covs,
    cc = TRUE,
    plot = FALSE,
    width = 500,
    height = 500) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("rmc requires jmvcore to be installed (restart may be required)")

    if ( ! missing(id)) id <- jmvcore::resolveQuo(jmvcore::enquo(id))
    if ( ! missing(dep)) dep <- jmvcore::resolveQuo(jmvcore::enquo(dep))
    if ( ! missing(covs)) covs <- jmvcore::resolveQuo(jmvcore::enquo(covs))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(id), id, NULL),
            `if`( ! missing(dep), dep, NULL),
            `if`( ! missing(covs), covs, NULL))

    for (v in id) if (v %in% names(data)) data[[v]] <- as.factor(data[[v]])

    options <- rmcOptions$new(
        id = id,
        dep = dep,
        covs = covs,
        cc = cc,
        plot = plot,
        width = width,
        height = height)

    analysis <- rmcClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

